import React, { useEffect, useRef } from 'react';
import '../styles/skills.css';
import Nav from '../components/Navigation';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark, faHtml5 } from '@fortawesome/free-solid-svg-icons';
import htmlImage from '../images/html.png';

function Skills() {
  const realAboutMeRef = useRef(null);
  const journeyTextRef = useRef(null);
  const observerRef = useRef(null);
  const intersectionObserverOptions = {
    threshold: 0.1
  };

  useEffect(() => {
    observerRef.current = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
          typeText(journeyTextRef.current);
          // Unobserve the target once it becomes visible
          observerRef.current.unobserve(entry.target);
        }
      },
      intersectionObserverOptions
    );

    if (realAboutMeRef.current) {
      observerRef.current.observe(realAboutMeRef.current);
    }

    return () => {
      if (observerRef.current && realAboutMeRef.current) {
        observerRef.current.unobserve(realAboutMeRef.current);
      }
    };
  }, []);

  const typeText = (element) => {
    const text = element.getAttribute('data-text');
    element.textContent = '';
    let index = 0;

    const type = () => {
      if (index < text.length) {
        element.textContent += text.charAt(index);
        index++;
        setTimeout(type, 50); // Adjust the typing speed here
      } else {
        element.classList.remove('typing-effect'); // Remove the typing effect class after completion
      }
    };

    type();
  };

  return (
    <section className='my-skill'>
      <div className='my-skills'>
        <div className='about-content'>
          <div className='page-container'>
            <div className='select-section'>
              <img src={htmlImage} alt="HTML Icon" /> AboutMe.html <FontAwesomeIcon icon={faXmark} />
            </div>
          </div>
          <div className='my-skills-content'>
            <div className='opacity-container'>
              <h1>RESUME</h1>
            </div>
            <div className='h1-absoloute'>
              <h1>ABOUT <span>ME</span></h1>
            </div>
          </div>
          <div className='my-skills-displayed'>
            {/* Skill circles */}
            {/* Sector wrapper code */}
          </div>
        </div>
        <div ref={realAboutMeRef} className='real-about-me fade-in-up'>
          <div className='my-skills-h1'>
            <h1>MY EXPERIENCE</h1>
          </div>
          <div className='my-journey'>
            <div className='my-journey-container'>
              <div className='my-journey-50'>
                <p ref={journeyTextRef} className="typing-effect" data-text="ojnsadjnsadnjsdnjsdnjods">ojnsadjnsadnjsdnjsdnjods</p>
              </div>
            </div>
          </div>
        </div>
        <Nav />
      </div>
    </section>
  );
}

export default Skills;
