import React, { useEffect, useRef } from 'react';
import '../styles/skills.css';
import Nav from '../components/Navigation';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark, faHtml5 } from '@fortawesome/free-solid-svg-icons';
import htmlImage from '../images/html.png';

function Skills() {
  const realAboutMeRef = useRef(null);
  const journeyTextRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
          typeText(journeyTextRef.current);
        }
      },
      { threshold: 0.1 }
    );

    if (realAboutMeRef.current) {
      observer.observe(realAboutMeRef.current);
    }

    return () => {
      if (realAboutMeRef.current) {
        observer.unobserve(realAboutMeRef.current);
      }
    };
  }, []);

  const typeText = (element) => {
    const text = element.textContent;
    element.textContent = '';
    let index = 0;

    const type = () => {
      if (index < text.length) {
        element.textContent += text.charAt(index);
        index++;
        setTimeout(type, 50); // Adjust the typing speed here
      } else {
        element.classList.remove('typing-effect'); // Remove the typing effect class after completion
      }
    };

    type();
  };

  return (
    <section className='my-skill'>
      <div className='my-skills'>
        <div className='about-content'>
          <div className='page-container'>
            <div className='select-section'>
              <img src={htmlImage} alt="HTML Icon" /> AboutMe.html <FontAwesomeIcon icon={faXmark} />
            </div>
          </div>
          <div className='my-skills-content'>
            <div className='opacity-container'>
              <h1>RESUME</h1>
            </div>
            <div className='h1-absoloute'>
              <h1>ABOUT <span>ME</span></h1>
            </div>
          </div>
          <div className='my-skills-displayed'>
            {/* Skill circles */}
            <div className='sector-wrapper'>
              <div className="circle percentage-90">
                <span>90%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>HTML</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-85">
                <span>85%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>CSS</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-50">
                <span>50%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>TAILWIND</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-81">
                <span>81%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>REACT</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-65">
                <span>65%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>PHP</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-65">
                <span>65%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>REACT NATIVE</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-52">
                <span>52%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>JAVASCRIPT</div>
            </div>
            <div className='sector-wrapper'>
              <div className="circle percentage-88">
                <span>88%</span>
                <div className="percentage-bar"></div>
              </div>
              <div className='text-holder'>FIGMA</div>
            </div>
          </div>
        </div>
        <div ref={realAboutMeRef} className='real-about-me fade-in-up'>
          <div className='my-skills-h1'>
            <h1>MY EXPERIENCE</h1>
          </div>
          <div className='my-journey'>
            <div className='my-journey-container'>
              <div className='my-journey-50'>
                <p ref={journeyTextRef} className="typing-effect">ojnsadjnsadnjsdnjsdnjods</p>
              </div>
            </div>
          </div>
        </div>
        <Nav />
      </div>
    </section>
  );
}

export default Skills;
