import React, { useState, useEffect } from 'react';
import '../styles/skills.css';
import Nav from '../components/Navigation';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark, faHtml5 } from '@fortawesome/free-solid-svg-icons';
import htmlImage from '../images/html.png';

function Skills() {
  const [percentage, setPercentage] = useState(0);

  useEffect(() => {
    const animationDuration = 2000; // Adjust duration in milliseconds

    let intervalId = setInterval(() => {
      if (percentage < 100) {
        setPercentage(percentage + 1);
      } else {
        clearInterval(intervalId);
      }
    }, animationDuration / 100);

    // Cleanup function to avoid memory leaks
    return () => clearInterval(intervalId);
  }, []); // Empty dependency array ensures animation runs only once

  return (
    <section className='my-skill'>
      <div className='my-skills'>
        <div className='about-content'>
          <div className='page-container'>
            <div className='select-section'>
              <img src={htmlImage} alt="HTML Icon" /> AboutMe.html <FontAwesomeIcon icon={faXmark} />
            </div>
          </div>
          <div className='my-skills-content'>
            <div className='opacity-container'>
              <h1>EXPERIENCE</h1>
            </div>
            <div className='h1-absoloute'>
              <h1>MY <span>SKILLS</span></h1>
            </div>
          </div>

          <div className='my-skills-displayed'>
            <div className='sector-wrapper'>
              <div className="circle percentage-100">
                <span>{percentage}%</span>
                <div className="percentage-bar" style={{ transform: `rotate(${percentage * 3.6}deg)` }}> {/* Animation using transform */}
                </div>
              </div>
              <div className='text-holder'>HTML & CSS</div>
            </div>
            <div className='sector-wrapper'> {/* Duplicate the structure for additional skills */}
              <div className="circle percentage-100">
                <span>{percentage}%</span>
                <div className="percentage-bar" style={{ transform: `rotate(${percentage * 3.6}deg)` }}>
                </div>
              </div>
              <div className='text-holder'>JavaScript</div>
            </div>
          </div>
        </div>
        <Nav />
      </div>
    </section>
  );
}

export default Skills;
